import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, database } from '../services/firebase';
import { ref, push, set } from 'firebase/database';

function HomePage() {
  const navigate = useNavigate();
  const [category, setCategory] = useState('');
  const [time, setTime] = useState('');
  const [place, setPlace] = useState('');
  const [image, setImage] = useState('');

  const signOut = async () => {
    console.log('Sign out button clicked');

    try {
      await auth.signOut();
      console.log('Successfully signed out');
      // Redirect to the login page
      navigate('/');
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const dbRef = ref(database, 'events');
    const newEventRef = push(dbRef);
    const newEventData = {
      category,
      time,
      place,
      image,
    };

    try {
      await set(newEventRef, newEventData);
      console.log('Event data successfully added to the database');
      // Reset form fields
      setCategory('');
      setTime('');
      setPlace('');
      setImage('');
    } catch (error) {
      console.error('Error adding event data to the database:', error);
    }
  };

  return (
    <div>
      <h1>Home Page</h1>
      <button onClick={signOut}>Sign Out</button>
      <form onSubmit={handleSubmit}>
        <label>
          Category:
          <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} />
        </label>
        <label>
          Time:
          <input type="text" value={time} onChange={(e) => setTime(e.target.value)} />
        </label>
        <label>
          Place:
          <input type="text" value={place} onChange={(e) => setPlace(e.target.value)} />
        </label>
        <label>
          Image:
          <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default HomePage;
