import { useEffect, useState } from 'react';
import { Card, Container, Button } from 'react-bootstrap';
import { ref, onValue, getDatabase, set } from 'firebase/database';
import { app, database } from '../services/firebase';
import './displayEvents.css';

function DisplayEvents() {
  const [events, setEvents] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [availableCategories, setAvailableCategories] = useState([]);

  useEffect(() => {
    const db = getDatabase(app);
    const eventsRef = ref(db, 'events');

    const fetchEvents = async () => {
      try {
        onValue(eventsRef, (snapshot) => {
          const data = snapshot.val();
          if (data) {
            const eventList = Object.values(data);
            setEvents(eventList);

            const categories = [...new Set(eventList.map((event) => event.category))];
            setAvailableCategories(categories);
          }
        });
      } catch (error) {
        console.error('Error fetching events data:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleApprove = async (eventId) => {
    const eventRef = ref(database, `events/${eventId}`);

    try {
      await set(eventRef, { approved: true }, { merge: true });
      console.log('Event approved');
    } catch (error) {
      console.error('Error approving event:', error);
    }
  };

  const handleDeny = async (eventId) => {
    const eventRef = ref(database, `events/${eventId}`);

    try {
      await set(eventRef, { approved: false }, { merge: true });
      console.log('Event denied');
    } catch (error) {
      console.error('Error denying event:', error);
    }
  };

  const filteredEvents = selectedCategory
    ? events.filter((event) => event.category === selectedCategory)
    : events;

  return (
    <Container className="mt-5">
      <h1>Display Events</h1>
      <div>
        <label htmlFor="categoryFilter">Filter by Category:</label>
        <select id="categoryFilter" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="">All</option>
          {availableCategories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
      <div className="card-container">
        {filteredEvents.map((event) => (
          <Card key={event.id} className="mb-3">
            <Card.Body>
              <Card.Title>{event.title}</Card.Title>
              <Card.Text>
                <strong>Category:</strong> {event.category}
              </Card.Text>
              <Card.Text>
                <strong>Time:</strong> {event.time}
              </Card.Text>
              <Card.Text>
                <strong>Place:</strong> {event.place}
              </Card.Text>
              <Card.Text>
                <strong>Image:</strong>
                <img src={event.img} alt={event.title} className="event-image" />
              </Card.Text>
              {event.approved ? (
                <div className="event-status">Approved</div>
              ) : (
                <div className="event-status">Pending Approval</div>
              )}
              {!event.approved && (
                <div className="event-actions">
                  <Button variant="success" onClick={() => handleApprove(event.id)}>
                    Approve
                  </Button>
                  <Button variant="danger" onClick={() => handleDeny(event.id)}>
                    Deny
                  </Button>
                </div>
              )}
            </Card.Body>
          </Card>
        ))}
      </div>
    </Container>
  );
}

export default DisplayEvents;
