// ...

const [approved, setApproved] = useState(false); // New state for approval status

// ...

const handleSubmit = async (event) => {
  event.preventDefault();

  const dbRef = ref(database, 'events');
  const newEventRef = push(dbRef);
  const newEventData = {
    title,
    category,
    time,
    place,
    img,
    approved: false, // Set the initial approval status to false
  };

  try {
    await set(newEventRef, newEventData);
    console.log('Event data successfully added to the database');
    // Reset form fields
    setTitle('');
    setCategory('');
    setTime('');
    setPlace('');
    setImg('');
    setApproved(false); // Reset the approval status
    // Navigate to the home page or any other desired page
  } catch (error) {
    console.error('Error adding event data to the database:', error);
  }
};

