import React, { useEffect, useState, useContext } from 'react';
import { Card, Container } from 'react-bootstrap';
import { ref, onValue, getDatabase } from 'firebase/database';
import { app } from '../services/firebase';
import { AuthContext } from '../services/auth'; 
import './displayEvents.css'; 

function DisplayEvents() {
  const [events, setEvents] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const { isAdmin } = useContext(AuthContext); // Use the useContext hook to access the isAdmin property

  useEffect(() => {
    if (isAdmin) {
      const db = getDatabase(app);
      const eventsRef = ref(db, 'events');

      const fetchEvents = async () => {
        try {
          onValue(eventsRef, (snapshot) => {
            const data = snapshot.val();
            if (data) {
              const eventList = Object.values(data);
              const filteredEventList = eventList.filter((event) => event.approved);
              setEvents(filteredEventList);
            }
          });
        } catch (error) {
          console.error('Error fetching events data:', error);
        }
      };

      fetchEvents();
    } else {
      setEvents([]); // Clear events if user is not an admin
    }
  }, [isAdmin]);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const filteredEvents = selectedCategory
    ? events.filter((event) => event.category === selectedCategory)
    : events;

  return (
    <Container className="mt-5">
      <h1>Display Events</h1>
      {isAdmin && (
        <div>
          <label htmlFor="categoryFilter">Filter by Category:</label>
          <select id="categoryFilter" value={selectedCategory} onChange={handleCategoryChange}>
            <option value="">All</option>
            <option value="Category 1">Category 1</option>
            <option value="Category 2">Category 2</option>
            <option value="Category 3">Category 3</option>
            {/* Add more options as needed */}
          </select>
        </div>
      )}
      <div className="card-container">
        {filteredEvents.map((event) => (
          <Card key={event.id} className="mb-3">
            <Card.Body>
              <Card.Title>{event.title}</Card.Title>
              <Card.Text>
                <strong>Category:</strong> {event.category}
              </Card.Text>
              <Card.Text>
                <strong>Time:</strong> {event.time}
              </Card.Text>
              <Card.Text>
                <strong>Place:</strong> {event.place}
              </Card.Text>
              <Card.Text>
                <strong>Image:</strong>
                <img src={event.img} alt={event.title} className="event-image" />
              </Card.Text>
            </Card.Body>
          </Card>
        ))}
      </div>
    </Container>
  );
}

export default DisplayEvents;
