import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, database } from '../services/firebase';
import { ref, push, set } from 'firebase/database';
import { Form, Button } from 'react-bootstrap';

function HomePage() {
  const navigate = useNavigate();
  const [category, setCategory] = useState('');
  const [time, setTime] = useState('');
  const [place, setPlace] = useState('');
  const [image, setImage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const dbRef = ref(database, 'events');
    const newEventRef = push(dbRef);
    const newEventData = {
      category,
      time,
      place,
      image,
    };

    try {
      await set(newEventRef, newEventData);
      console.log('Event data successfully added to the database');
      // Reset form fields
      setCategory('');
      setTime('');
      setPlace('');
      setImage('');
    } catch (error) {
      console.error('Error adding event data to the database:', error);
    }
  };

  return (
    <div>
      <h1>Home Page</h1>

      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="category">
          <Form.Label>Category:</Form.Label>
          <Form.Control
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            placeholder="Enter category"
          />
        </Form.Group>

        <Form.Group controlId="time">
          <Form.Label>Time:</Form.Label>
          <Form.Control
            type="text"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            placeholder="Enter time"
          />
        </Form.Group>

        <Form.Group controlId="place">
          <Form.Label>Place:</Form.Label>
          <Form.Control
            type="text"
            value={place}
            onChange={(e) => setPlace(e.target.value)}
            placeholder="Enter place"
          />
        </Form.Group>

        <Form.Group controlId="image">
          <Form.Label>Image:</Form.Label>
          <Form.Control
            type="text"
            value={image}
            onChange={(e) => setImage(e.target.value)}
            placeholder="Enter image URL"
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default HomePage;
