import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAuth, signOut as firebaseSignOut } from 'firebase/auth';
import { ref, onValue, getDatabase, remove } from 'firebase/database';
import { Container, Button } from 'react-bootstrap';
import DisplayEvents from '../components/displayEvents';

function Admin() {
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  
  useEffect(() => {
    const db = getDatabase();
    const eventsRef = ref(db, 'events');

    const fetchEvents = () => {
      onValue(eventsRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
          const eventList = Object.entries(data).map(([id, eventData]) => ({ id, ...eventData }));
          setEvents(eventList);
          setFilteredEvents(eventList);
        } else {
          setEvents([]);
          setFilteredEvents([]);
        }
      });
    };

    fetchEvents();
  }, []);

  const signOut = async () => {
    console.log('Sign out button clicked');

    const auth = getAuth();
    try {
      await firebaseSignOut(auth);
      console.log('Successfully signed out');
      // Redirect to the login page
      navigate('/');
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  const handleApprove = async (eventId) => {
    const db = getDatabase();
    const eventRef = ref(db, `events/${eventId}`);

    try {
      await eventRef.update({ approved: true });
      console.log('Event approved');
    } catch (error) {
      console.error('Error approving event:', error);
    }
  };

  const handleDeny = async (eventId) => {
    const db = getDatabase();
    const eventRef = ref(db, `events/${eventId}`);

    try {
      await remove(eventRef);
      console.log('Event denied');
    } catch (error) {
      console.error('Error denying event:', error);
    }
  };

  return (
    <Container className="mt-5">
      <h1>Admin Page</h1>
      <Button variant="primary" onClick={signOut}>Sign Out</Button>

      <h2>Events Approval</h2>
      <div>
        {filteredEvents.map((event) => (
          <div key={event.id}>
            <h3>{event.title}</h3>
            {/* Display other event details */}
            {event.approved ? (
              <div>Approved</div>
            ) : (
              <div>
                <Button variant="success" onClick={() => handleApprove(event.id)}>
                  Approve
                </Button>
                <Button variant="danger" onClick={() => handleDeny(event.id)}>
                  Deny
                </Button>
              </div>
            )}
          </div>
        ))}
      </div>

      <h2>Display Events</h2>
      <DisplayEvents setFilteredEvents={setFilteredEvents} />
    </Container>
  );
}

export default Admin;
